mapper.select:
select

  distinct


from menu

  


  order by ${orderByClause}


  limit #{startRow} , #{pageSize}

SELECT a.id, a.parent_id AS "parent.id", a.name, a.href, a.icon, a.sort, a.is_show isShow, a.permission, a.remarks, a.is_deleted isDeleted, p.name AS "parent.name" FROM menu a LEFT JOIN menu p ON p.id = a.parent_id
a.is_deleted = 'N' AND a.is_show = 1
AND a.name = #{name}
ORDER BY a.sort
limit #{start} , #{limit}
